<?php

namespace AppBundle\Repository;

/**
 * CheckOutPromptRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class CheckOutPromptRepository extends \Doctrine\ORM\EntityRepository
{

    /**
     * @return array
     */
    public function findAllOrderedBySort()
    {
        return $this->getEntityManager()
            ->createQuery('SELECT cp
                FROM AppBundle:CheckOutPrompt cp
                ORDER BY cp.sort, cp.name ASC')
            ->getResult();
    }
}
