<?php

namespace AppBundle\Repository;

/**
 * ContactFieldRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class ContactFieldRepository extends \Doctrine\ORM\EntityRepository
{

    /**
     * @return array
     */
    public function findAllOrderedBySort()
    {
        return $this->getEntityManager()
            ->createQuery('SELECT f FROM AppBundle:ContactField f ORDER BY f.sort, f.name ASC')
            ->getResult();
    }

}
