<?php

namespace AppBundle\Repository;

/**
 * ProductFieldRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class ProductFieldRepository extends \Doctrine\ORM\EntityRepository
{

    /**
     * @return array
     */
    public function findAllOrderedBySort()
    {
        return $this->getEntityManager()
            ->createQuery('SELECT f FROM AppBundle:ProductField f ORDER BY f.sort, f.name ASC')
            ->getResult();
    }

}
