<?php

namespace AppBundle\Repository;

/**
 * ProductFieldSelectOptionRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class ProductFieldSelectOptionRepository extends \Doctrine\ORM\EntityRepository
{

    /**
     * @return array
     */
    public function findAllOrderedBySort($fieldId)
    {
        return $this->getEntityManager()
            ->createQuery('SELECT f
                FROM AppBundle:ProductFieldSelectOption f
                WHERE f.productField = '.$fieldId.'
                ORDER BY f.sort, f.optionName ASC')
            ->getResult();
    }

}
